class Otp extends StatefulWidget {
  String verificationId;
  final String phoneNumber;
   Otp({
    super.key,
    required this.verificationId,
    required this.phoneNumber,
  });

  @override
  State<Otp> createState() => _OtpState();
}

class _OtpState extends State<Otp> {
  int seconds = 30;
  Timer? timer;
  bool canResend = false;
  late var appSignatureId;
  bool isLoading = false;

  @override
  void initState() {
    super.initState();
    startTimer();
    // smsAutoFill();
  }

  @override
  void dispose() {
    timer?.cancel();
    super.dispose();
  }

  // Future<void> smsAutoFill() async{
  //   var id = await SmsAutoFill().getAppSignature;
  //   setState((){
  //     appSignatureId = id;
  //   });
  // }

  void startTimer() {
    timer = Timer.periodic(const Duration(seconds: 1), (t) {
      if (seconds == 0) {
        t.cancel();
        setState(() => canResend = true);
      } else {
        setState(() => seconds--);
      }
    });
  }

  Future<void> verifyOtp(String verificationId, String smsCode) async {
    PhoneAuthCredential credential = PhoneAuthProvider.credential(
      verificationId: verificationId,
      smsCode: smsCode,
    );
    try {
      await _auth.signInWithCredential(credential);
      Get.snackbar("Success", "Otp verification successful");
    } catch (e) {
      print(e.toString());
      Get.snackbar("Otp verification failed", "${e.toString()}");
    }
  }


  Future<void> resendOtp() async {
    // setState(() => isLoading = true);
    try {
     
      await _auth.verifyPhoneNumber(
        phoneNumber: '+233${widget.phoneNumber}',

        verificationCompleted: (credential) async {
          // setState(() => isLoading = false);
        },

        verificationFailed: (FirebaseAuthException e) {
          setState(() => isLoading = false);
          Get.snackbar("Error", e.message ?? "Verification failed");
        },

        codeSent: (String verId, int? resendToken) {
          setState(() {
            isLoading = false;
            widget.verificationId = verId;
          });
          startTimer();

          /// âœ… Navigate ONLY after reCAPTCHA succeeds
        
        },

        codeAutoRetrievalTimeout: (String verId) {
          widget.verificationId = verId;
          setState(() => isLoading = false);
        },
      );
    } catch (e) {
      Get.snackbar("Otp unsuccessful", "Check your internet connection");
    }
  }


  final TextEditingController _otpController = TextEditingController();

  FirebaseAuth _auth = FirebaseAuth.instance;

  bool _codeSent = false;

  
